import React, {Component} from 'react';
import axios from 'axios';

export default class Vendor extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            price: '',
            quantity: '',
            vendor: this.props.location.vendor_id
        }

        this.onChangeName = this.onChangeName.bind(this);
        this.onChangePrice = this.onChangePrice.bind(this);
        this.onChangeQuantity = this.onChangeQuantity.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.addProduct = this.addProduct.bind(this);
        this.rendor = '0'
    }

    onChangeName(event) {
        this.setState({ name: event.target.value });
    }

    onChangePrice(event) {
        this.setState({ price: event.target.value });
    }

    onChangeQuantity(event) {
        this.setState({ quantity: event.target.value });
    }

    addProduct(event) {
        // console.log(this.rendor)
        this.rendor = '1'
        // axios.get(`http://localhost:4000/:${this.rendor}`)
        // .then ( response => {

        // })
        // .catch(function(error) {
        //     console.log(error);
        // })
    }

    onSubmit(e) {
        e.preventDefault();

        const newProduct = {
            name: this.state.name,
            price: this.state.price,
            quantity: this.state.quantity,
            vendor: this.state.vendor
        }

        axios.post('http://localhost:4000/add_product', newProduct)


        this.setState({
            name: '',
            price: '',
            quantity: ''
        });
    }

    render() {
        console.log(this.rendor)
        if (this.rendor == '0') {
            return this.render1();
        }

        else if (this.rendor == '1') {
            console.log("yes")

            return this.render2();
        }

    }

    render1() {
        return (
            <div>
                <button onClick = {this.addProduct}>Add Product</button>
                <button >All Products</button>
                <button >Product ready to dispatch</button>
                <button >Dispatched product</button>
                <button >Logout</button>
            </div>   
        )
    }

    render2() {
        return (
            <div>
                <button onClick = {this.addProduct}>Add Product</button>
                <button >All Products</button>
                <button >Product ready to dispatch</button>
                <button >Dispatched product</button>
                <button >Logout</button>

                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Name: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.name}
                               onChange={this.onChangename}
                               />
                    </div>
                    <div className="form-group">
                        <label>Price: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.price}
                               onChange={this.onChangePrice}
                               />  
                    </div>
                    <div className="form-group">
                        <label>Quantity: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.quantity}
                               onChange={this.onChangeQuantity}
                               />  
                    </div>                    
                    <div className="form-group">
                        <input type="submit" value="addProduct" className="btn btn-primary"/>
                    </div>
                </form>
            </div>   
        )
    }
}
