import React, {Component} from 'react';
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css"
/* eslint-disable */ 

export default class Vendor extends Component {

    constructor(props) {
        super(props);

        this.state = {
            name: '',
            price: '',
            quantity: '',
            status: '0',
            vendor: this.props.location.vendor,
            vendor_name: this.props.location.vendor_name,
            rendor: '0',
            products: [],
            ready_products: [],
            dispatch_products: []
        }
        this.onChangeName = this.onChangeName.bind(this);
        this.onChangePrice = this.onChangePrice.bind(this);
        this.onChangeQuantity = this.onChangeQuantity.bind(this);
    }

    onChangeName(event) {
        this.setState({ name: event.target.value });
    }

    onChangePrice(event) {
        this.setState({ price: event.target.value });
    }

    onChangeQuantity(event) {
        this.setState({ quantity: event.target.value });
    }

    addProduct = (e) => {
        this.setState({rendor : '1'});
        return this.render()
    }

    allProduct = (e) => {
        const newProduct = {
            vendor: this.state.vendor,
            status: '0'
            }

        axios.post('http://localhost:4000/all_product', newProduct)
             .then(response => {
                this.setState({products: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
        this.setState({rendor : '2'});

    }

    Productready = (e) => {
        const newProduct = {
            vendor: this.state.vendor,
            status: '1'
            }

        axios.post('http://localhost:4000/product_ready', newProduct)
            .then(response => {
                this.setState({ready_products: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
        this.setState({rendor : '3'});

    }


    Productdispatch= (e) => {

        console.log('HIIIIII')
        const newProduct = {
            vendor: this.state.vendor,
            status: '2'
            }

        axios.post('http://localhost:4000/product_dispatch', newProduct)
            .then(response => {
                this.setState({dispatch_products: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
        this.setState({rendor : '4'});

    }

    Delete = (e,Pid) => {
        const pid = {
            id: Pid
        }
        axios.post('http://localhost:4000/delete_product', pid)
        .then(response => {
            axios.get(`http://localhost:4000/list/:${this.state.vendor}`)
            .then(response => {
                this.setState({products: response.data});
            })
            .catch(function(error) {
                 console.log(error);
            })            
        })
    }

    Dispatch = (e,Pid) => {

        const pid = {
            id: Pid
        }
        axios.post('http://localhost:4000/dispatch_product', pid)
        .then(response => {
            const newProduct = {
            vendor: this.state.vendor,
            status: '1'
            }
            axios.post('http://localhost:4000/product_ready', newProduct)
            .then(response => {
                this.setState({ready_products: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
        })
    }

    Logout = (e) => {
        this.props.history.push({
