import React, {Component} from 'react';
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css"
/* eslint-disable */ 

export default class Customer extends Component {

    constructor(props) {
        super(props);
        this.state = {
            product_name: '',
            product: '',
            price: 0,
            quantity: 0,
            vendor_name: '',
            vendor: '',
            status: '0',
            customer_name: this.props.location.customer_name,
            customer: this.props.location.customer,
            rating: 0,
            review: '',
            rendor: '0',
            search: '',
            search_result: []
        }

        this.onChangeSearch = this.onChangeSearch.bind(this);
    }

    onChangeSearch(event) {
        this.setState({ search: event.target.value });
    }

    searchproduct = (e) => {
        this.setState({rendor : '1'});
        return this.render()
    }

    Logout = (e) => {
        this.props.history.push({
            pathname: '/login',
        });
    };

    onSubmit = (e) => {
        e.preventDefault();

        const newProduct = {
            name: this.state.search,
        }

        axios.post('http://localhost:4000/search_product', newProduct)
            .then(response => {
                this.setState({search_result: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })

        this.setState({
            search: '',
        });
        this.setState({rendor : '2'}); 
    }

    Order = (e,Pid) => {
        const pid = {
            id: Pid
        }
        axios.post('http://localhost:4000/delete_product', pid)
        .then(response => {
            axios.get(`http://localhost:4000/list/:${this.state.vendor}`)
            .then(response => {
                this.setState({products: response.data});
            })
            .catch(function(error) {
                 console.log(error);
            })            
        })
    }

    render() {
        if (this.state.rendor ==='0') {
            return this.render1();
        }

        else if (this.state.rendor === '1') {
            return this.render2();
        }

        else if (this.state.rendor === '2') {
            return this.render3();
        }

        else if (this.state.rendor === '3') {
            return this.render4();
        }

        else if (this.state.rendor === '4') {
            return this.render5();
        }

    }

    render1() {
        return (
            <div className="container">
                <div class="topnav">
                    <a onClick = {e => this.searchproduct(e)} href="#" >Search Product</a>
                    <a onClick = {e => this.allOrders(e)} href="#" >Orders Status</a>
                    <a onClick = {e => this.Productconfirmed(e)} href="#" >Confirmed Orders</a>
                    <a onClick = {e => this.Logout(e)} href="#" >Logout</a>
                </div> 
            </div>   
        )
    }

    render2() {
        return (
            <div className="container">
                <div class="topnav">
                    <a onClick = {e => this.searchproduct(e)} href="#" class="active">Search Product</a>
                    <a onClick = {e => this.allOrders(e)} href="#" >Orders Status</a>
                    <a onClick = {e => this.Productconfirmed(e)} href="#" >Confirmed Orders</a>
                    <a onClick = {e => this.Logout(e)} href="#" >Logout</a>
                </div>
                <div>  
                    <form onSubmit= {e => this.onSubmit(e)}>
                        <div className="form-group">
                            <label>Product: </label>
                            <input type="text" 
                                   className="form-control" 
                                   value={this.state.search}
                                   onChange={this.onChangeSearch}
                                   />
                        </div>           
                        <div className="form-group">
                            <input type="submit" value="Search" className="btn btn-primary"/>
                        </div>
                    </form>
                </div>

            </div>   
        )
    }

    render3() {
        return (
            <div className="container">
                <div class="topnav">
                    <a onClick = {e => this.searchproduct(e)} href="#" class="active">Search Product</a>
                    <a onClick = {e => this.allOrders(e)} href="#" >Orders Status</a>
                    <a onClick = {e => this.Productconfirmed(e)} href="#" >Confirmed Orders</a>
                    <a onClick = {e => this.Logout(e)} href="#" >Logout</a>
                </div>
                <div>  
                    <form onSubmit= {e => this.onSubmit(e)}>
                        <div className="form-group">
                            <label>Product: </label>
                            <input type="text" 
                                   className="form-control" 
                                   value={this.state.search}
                                   onChange={this.onChangeSearch}
                                   />
                        </div>           
                        <div className="form-group">
                            <input type="submit" value="Search" className="btn btn-primary"/>
                        </div>
                    </form>
                </div>

                <div>
                    <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>product Name</th>
                            <th>Price</th>
                            <th>Quantity Remaining</th>
                            <th>Vendor Name</th>
                            <th>Order</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.search_result.map((currentProduct, i) => {
                            return (
                                <tr>
                                    <td>{currentProduct.name}</td>
                                    <td>{currentProduct.price}</td>
                                    <td>{currentProduct.quantity}</td>
                                    <td>{currentProduct.vendor_name}</td>
                                    <td> <button onClick = {e => this.Order(e,currentProduct._id)}> Order </button> </td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
                </div>

            </div>   
        )
    }




}
