    isInt = function(value) {
        return !isNaN(value) && (function(x) { return (x | 0) === x; })(parseFloat(value))
    }

    Edit_order_portal = (e,x) => {
        this.setState({current_order: x})
        this.setState({rendor : '8'});
    }

    Edit_order_portal1 = (e) => {
        if (this.isInt(this.state.edit_quantity)) {
            alert("Please enter an Integer")
        }
        else{
            var quan = (parseInt(this.state.current_order.quantity_rem) + parseInt(this.state.current_order.quantity))
            if (parseInt(quan) < parseInt(this.state.edit_quantity) | parseInt(this.state.edit_quantity) < 1) {
                var v = `Ordered Quantity should be in range 0 - ${quan}`
                alert(v)
                this.setState({rendor : '8'});
            }
            else {   
                const x = (parseInt(this.state.current_order.quantity_rem)  + parseInt(this.state.current_order.quantity) - parseInt(this.state.edit_quantity))
                var y = 0
                if (x === 0) {
                    y = 1
                }

                // console.log(x,y)

                const data = {
                    id: this.state.current_order.product,
                    quantity: x,
                    status: y
                }

                // console.log(data)
                axios.post('http://localhost:4000/update_product', data)
                .then(response => {
                    const newOrder = {
                        id: this.state.current_order._id,
                        quantity: this.state.edit_quantity,
                        status: y,
                        quantity_rem: x
                    }

                    axios.post('http://localhost:4000/edit_order', newOrder)
                    .then(response => {
                        this.setState({
                            quantity: 0,
                            status: '',
                            quantity_rem: 0

                        });

                        const data1 = {
                            id: this.state.current_order.product,
                            status: y,
                            quantity_rem: x
                        }
                        axios.post('http://localhost:4000/update_order', data1)
                        .then (response => {
                            const newProduct = {
                                customer: this.state.customer
                                }

                            axios.post('http://localhost:4000/all_order', newProduct)
                                .then(response => {
                                    this.setState({allorders: response.data});
                                })
                                .catch(function(error) {
                                    console.log(error);
                                })
                        })
                    })    
                })

                alert('Order edited Successfully')
                this.setState({rendor : '4'});
            }
        }

    }

    Order1 = (e) => {

        if(isNaN(this.state.o_quantity)) {
            alert("Please enter an integer")
        }
        else {
            var quan =  parseInt(this.state.current_pid.quantity)
            if((parseInt(quan) < parseInt(this.state.o_quantity)) | parseInt(this.state.o_quantity) < 1)
            {   
                var v = `Ordered Quantity should be in the range 0 - ${quan}`
                alert(v)
                this.setState({rendor : '3'});
            }
            else
            {   
                const x = (parseInt(this.state.current_pid.quantity) - parseInt(this.state.o_quantity))
                var y = 0
                if (x === 0) {
                    y = 1
                }

                // console.log(x,y)

                const data = {
                    id: this.state.current_pid._id,
                    quantity: x,
                    status: y
                }

                // console.log(data)
                axios.post('http://localhost:4000/update_product', data)
                .then(response => {
                    const newOrder = {
                        product_name: this.state.current_pid.name,
                        product: this.state.current_pid._id,
                        price: this.state.current_pid.price,
                        quantity: this.state.o_quantity,
                        vendor_name: this.state.current_pid.vendor_name,
                        vendor: this.state.current_pid.vendor,
                        status: y,
                        customer_name: this.state.customer_name,
                        customer: this.state.customer,
                        rating: 0,
                        review: '',
                        quantity_rem: x
                    }

                    axios.post('http://localhost:4000/add_order', newOrder)
                    .then(response => {
                        this.setState({
                            product_name: '',
                            product: '',
                            price: 0,
                            quantity: 0,
                            vendor_name: '',
                            vendor: '',
                            status: '',
                            rating: 0,
                            review: '',
                            quantity_rem: 0
                        });

                        const data1 = {
                            id: this.state.current_pid._id,
                            status: y,
                            quantity_rem: x
                        }
                        axios.post('http://localhost:4000/update_order', data1)
                    })    
                })

                alert('Ordered Successfully')
                this.setState({rendor : '1'});
            }    
        }


    }

