                                   value={this.state.rating_v}
                                   onChange={this.onChangeRating_v}
                                   />
                        </div>           
                        <div className="form-group">
                            <input type="button" value="Submit" onClick = {e => this.Rnr1_v(e)} className="btn btn-primary"/>
                        </div>
                    </form>
                </div>
            </div>   
        )
    }
}
