import React, {Component} from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css"


export default class Register extends Component {

    constructor(props) {
        super(props);

        this.state = {
            username: '',
            password: '',
            user_type: "customer"
        }

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeUser_type = this.onChangeUser_type.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChangeUsername(event) {
        this.setState({ username: event.target.value });
    }

    onChangePassword(event) {
        this.setState({ password: event.target.value });
    }

    onChangeUser_type(event) {
        this.setState({ user_type: event.target.value });
    }

    onSubmit(e) {
        e.preventDefault();

        const newUser = {
            username: this.state.username,
            password: this.state.password,
            user_type: this.state.user_type
        }
        axios.post('http://localhost:4000/add', newUser)
             // .then(res => {
                // console.log(res.data);
                // if(res.data )
                // });

        this.setState({
            username: '',
            password: '',
            user_type: ''
        });
    }

    render() {
        return (
            <div>
                <div className="container">
                    <nav className="navbar navbar-expand-lg navbar-light bg-light">
                      <Link to="/" className="navbar-brand">App</Link>
                      <div className="collapse navbar-collapse">
                        <ul className="navbar-nav mr-auto">
                          <li className="navbar-item">
                            <Link to="/login" className="nav-link">Login</Link>
                          </li>
                          <li className="navbar-item">
                            <Link to="/register" className="nav-link">Register</Link>
                          </li>
                        </ul>
                      </div>
                    </nav>
                </div>    
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.username}
                               onChange={this.onChangeUsername}
                               />
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="password" 
                               className="form-control"
                               value={this.state.password}
                               onChange={this.onChangePassword}
                               />  
                    </div>
                    <div className="form-group">
                        <label>User type: </label>
                        <select className="form-control" value={this.state.user_type} onChange={this.onChangeUser_type}>
                            <option value="customer">Customer</option>
                            <option value="vendor">Vendor</option>
                        </select>    
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
